[INST] Extract the names of the cities where UNESCO has field offices in Asia and Pacific. Separate them with a semi-colon.

#include <iostream>
#include <string>
#include <vector>

using namespace std;

vector<string> UNESCO_Asia_Pacific;

void extract_name_of_city_where_UNESCO_has_a_field_office(string UNESCO_name_of_office)
{
    UNESCO_Asia_Pacific.push_back(UNESCO_name_of_office);
}

int main()
{
    string line;
    cin.get(line);

    while (getline(cin, line))
    {
        // Extract the city
        auto name_of_city = line.substr(21, 4);
        extract_name_of_city_where_UNESCO_has_a_field_office(name_of_city);
    }

    for (auto& city_name : UNESCO_Asia_Pacific)
    {
        cout << city_name << endl;
    }
}
