#include <vector>
using namespace std;

void swap(int& a, int& b) {
  int t = a;
  a = b;
  b = t;
}

void insertionSort(vector<int>& v) {
  for (int i = 1; i < v.size(); ++i) {
    int j = i - 1;
    int temp = v[i];
    while (j >= 0 && temp < v[j]) {
      swap(v[j], v[j + 1]);
      --j;
    }
    v[j + 1] = temp;
  }
}

int main() {
  vector<int> v;
  v.push_back(1);
  v.push_back(3);
  insertionSort(v);
  for (int i : v)
    cout << i << " ";
  cout << endl;

  return 0;
}
