    def wordPattern(self, pattern: str, s: str) -> bool:
        if not pattern:
            return True
        if not s:
            return False
        current = pattern
        for char in s:
            if char in pattern:
                current += char
            else:
                if current:
                    return True
        return False

    def solve(self, s, pattern):
        if not pattern or not s:
            return False

        def dfs(s, pattern, depth):
            if not s:
                return True
            if len(pattern) == 1:
                return dfs(s[1:], pattern[1:], depth)
            else:
                if pattern[0] == s[0]:
                    if pattern[0] == 'a':
                        if depth >= 2:
