                    dfs(s[1:], pattern[1:], depth + 1)
                elif pattern[0] == 'd':
                    if depth >= 2:
                        return True
                    dfs(s[1:], pattern[1:], depth + 1)
                else:
                    if depth >= 2:
                        return True
                    dfs(s[1:], pattern[1:], depth + 1)

        return dfs(s, pattern, 0)

